import React, { useState, useEffect } from 'react';
import { Route, Switch, useHistory } from 'react-router-dom';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import ImagePopup from './ImagePopup'
import EditProfilePopup from './EditProfilePopup'
import EditAvatarPopup from './EditAvatarPopup'
import AddPlacePopup from './AddPlacePopup'
import { CurrentUserContext } from '../contexts/CurrentUserContext'
import api from '../utils/Api'
import Register from './Register';
import Login from './Login';
import { register, authorize, getContent } from '../auth';
import ProtectedRoute from './ProtectedRoute';


function App() {

  const [loggedIn, setLoggedIn] = useState(false)
  const [userData, setUserData] = useState({
    email: ''
  })

  const history = useHistory();

  useEffect(_ => {
    tokenCheck()
  }, [])

  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false)
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false)
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false)
  const [selectedCard, setSelectedCard] = useState({});
  const [isCardPopupOpen, setCardPopupOpen] = useState(false)
  const [currentUser, setcurrentUser] = useState({})
  const [cards, getCards] = useState([])

  useEffect(() => {
    api.getInitialCards()
      .then((data) => {
        const items = data.map((el) => ({
          _id: el._id,
          name: el.name,
          link: el.link,
          likes: el.likes,
          owner: el.owner
        }))

        getCards(items);
      })
      .catch(err => console.log(err))
  }, [])

  useEffect(() => {
    api.getUserInfo()
      .then((data) => {
        setcurrentUser(data)
      })
      .catch(err => console.log(err))
  }, [])

  function handleCardClick(card) {
    setCardPopupOpen(true)
    setSelectedCard(card)
  }

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true)
  }

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true)
  }

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true)
  }

  function closeAllPopups() {
    setEditAvatarPopupOpen(false)
    setAddPlacePopupOpen(false)
    setEditProfilePopupOpen(false)
    setCardPopupOpen(false)
  }

  function handleCardDelete(id) {
    api.deleteCard(id)
      .then(() => {
        getCards(cards.filter(card => card._id !== id))
      })
      .catch(err => console.log(err))
  }

  function handleCardDislike(id) {
    api.removeLike(id)
      .then((newCard) => {
        const newCards = cards.map((c) => c._id === id ? newCard : c);
        getCards(newCards);
      })
      .catch(err => console.log(err))
  }

  function handleCardLike(id) {
    api.putLike(id)
      .then((newCard) => {
        const newCards = cards.map((c) => c._id === id ? newCard : c);
        getCards(newCards);
      })
      .catch(err => console.log(err))
  }

  function handleUpdateUser(newData) {
    api.patchUserInfo(newData)
      .then((data) => {
        setcurrentUser(data)
        closeAllPopups()
      })
      .catch(err => console.log(err))
  }

  function handleUpdateAvatar(newAvatar) {
    api.patchAvatar(newAvatar)
      .then((data) => {
        setcurrentUser(data)
        closeAllPopups()
      })
      .catch(err => console.log(err))
  }

  function handleAddPlaceSubmit(newCard) {
    api.postCard(newCard)
      .then((data) => {
        getCards([...cards, data]);
        closeAllPopups()
      })
      .catch(err => console.log(err))
  }

  const handleLogin = (email, password) => {
    authorize(email, password)
      .then(data => {
        if (data.token) {
          localStorage.setItem('jwt', data.token)
          setUserData({
            email
          })
        }
      })
      .catch(err => console.error(err))
  }

  const handleRegister = (email, password) => {
    register(email, password)
      .then(data => console.log(data))
      .catch(err => console.error(err))

  }

  const tokenCheck = () => {
    const jwt = localStorage.getItem('jwt');
    if (jwt) {
      getContent(jwt)
        .then((res) => {
          if (res.data.email)
            setUserData({
              email: res.data.email
            })
            setLoggedIn(true)


        }).catch(err => console.error(err))
    }
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='page'>
        <div className="page__content">
          <Header />
          <Switch>
            <Route exact path="/sign-in">
              <Login tokenCheck={tokenCheck} handleLogin={handleLogin} />
            </Route>
            <Route exact path="/sign-up">
              <Register handleRegister={handleRegister} />
            </Route>
            <ProtectedRoute exact path="/"

              loggedIn={loggedIn}

              component={Main}
              onEditAvatar={handleEditAvatarClick}
              onEditProfile={handleEditProfileClick}
              onAddPlace={handleAddPlaceClick}
              onCardClick={handleCardClick}
              onCardDelete={handleCardDelete}
              onCardLike={handleCardLike}
              onCardDislike={handleCardDislike}
              cards={cards}
            />


          </Switch>
          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
          />
          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
          />
          <AddPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            onAddPlace={handleAddPlaceSubmit}
          />
          <ImagePopup
            card={selectedCard}
            isOpen={isCardPopupOpen}
            onClose={closeAllPopups}
          />




          <Footer />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
