import React from 'react';

function ImagePopup(props) {
	console.log(props.card)
	return (
		<section className={`pop-up popup_type_image ${props.isOpen && 'pop-up_type_opened'}`}>
		<div className="pop-up__content">
			<div className="pop-up__image-contnaiter">
				<button className="pop-up__btn pop-up__btn_action_deny hover-button" type="button" onClick={props.onClose}>
				</button>
				<img className="pop-up__image" src={props.src} alt='изображение' />
				<p className='pop-up__image-name'>{props.card}</p>
			</div>
		</div>
	</section>
	)
}

export default ImagePopup;
