import React from 'react'
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Card(props) {

	const { card, src, title, likes } = props;
	const currentUser = React.useContext(CurrentUserContext);
	const isOwnCard = card.owner._id === currentUser._id;
	const cardDeleteButtonClassName = (
		`photo-grid__trash-button hover-button ${isOwnCard ? 'photo-grid__trash-button_visible' : ''}`
	);
	const isLiked = card.likes.some(i => i._id === currentUser._id);
	const cardLikeButtonClassName = (
		`photo-grid__like-button hover-button ${isLiked ? 'photo-grid__like-button_type_active' : ''}`
	);

	function handleClick() {
		props.onCardClick(card);
	}
	return (
		<div className="photo-grid__item">
			{isOwnCard && <button type="button" className='card__delete-button' onClick={handleDeleteClick}></button>}
			<button className={cardDeleteButtonClassName} type="button">
			</button>
			<div style={{ backgroundImage: `url(${src})` }} className="photo-grid__image photo-grid__image-button hover-button" type='button' onClick={handleClick}></div>

			<div className="photo-grid__description">
				<h2 className="photo-grid__place">{title}</h2>
				<div className="photo-grid__like-wrapper">
					<button className={cardLikeButtonClassName} type="button"></button>
					<p className="photo-grid__likes-count">{likes.length}</p>
				</div>
			</div>
		</div>
	)
}

export default Card;
