import React from 'react';
import PopupWithForm from './PopupWithForm'


function AddPlacePopup(props) {
	const { isOpen, onClose } = props;

	return (
		<PopupWithForm isOpen={isOpen} onClose={onClose} name='edit' title='Редактировать профиль' onSubmit={handleSubmit}>
			<input id='name-input' type="text" className="pop-up__input pop-up__text" name="name" required minLength="2" maxLength="40" autoComplete="off" value={name} onChange={handleNameChange}/>
			<span id="name-input-error" className=""></span>
			<input id='profession-input' type="text" className="pop-up__input pop-up__text " name="about"
				required minLength="2" maxLength="200" autoComplete="off" value={description} onChange={handleDiscriptionChange}/>
			<span id="profession-input-error" className=""></span>
		</PopupWithForm>
	)
}

export default AddPlacePopup;












